//WAP to print following pattern 
/* 
 4444
 4444
 4444
 4444
 for n=4
 */

package javaAssignment1;

public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
		for(int i=1;i<=n;i++) {
			for(int j=1;j<=n;j++) {
				System.out.print(4);
			}
			System.out.println();
		}
	}

}
